import React, { useState } from 'react'
import { popBestSelling, popOnSale, popTopRated } from './Array'

const Popular = () => {
    const [best, setBest] = useState(popBestSelling);
    const [sale, setSale] = useState(popOnSale);
    const [rated, setRated] = useState(popTopRated);
    return (
        <div className="popular">
            <div className="container">
                <div className="row">
                    <div className="col">
                        <h2 className='heading'>Popular of the Year</h2>
                    </div>
                </div>

                <div className="row">

                    <div className="col-md-6 col-lg-4 row">
                        <div className="col d-flex align-items-end pb-2 ">
                            <h5>On Sale</h5>

                        </div>
                        <div className="row gx-0 mb-2 gy-2">
                            {
                                sale.map((item) => {
                                    const { id, img, name, price } = item;
                                    return (<><div className="col-4">
                                        <img src={img} alt="" />
                                    </div>
                                        <div className="col-8 pt-3">
                                            <div>{name}</div>
                                            <div>{price} $</div>
                                        </div></>)
                                })
                            }


                        </div>
                    </div>

                    <div className="col-md-6 col-lg-4 row">
                        <div className="col d-flex align-items-end pb-2 ">
                            <h5>Top Rated</h5>

                        </div>
                        <div className="row gx-0 mb-2 gy-2">
                            {
                                rated.map((item) => {
                                    const { id, img, name, price } = item;
                                    return (<><div className="col-4">
                                        <img src={img} alt="" />
                                    </div>
                                        <div className="col-8 pt-3">
                                            <div>{name}</div>
                                            <div>{price} $</div>
                                        </div></>)
                                })
                            }


                        </div>
                    </div>

                    <div className="col-md-6 col-lg-4 row">
                        <div className="col d-flex align-items-end pb-2 ">
                            <h5>Best Selling</h5>

                        </div>
                        {
                            best.map((item) => {
                                const { id, img, name, price } = item;
                                return (
                                    <div className="row gx-0 mb-2 gy-2">
                                        <div className="col-4">
                                            <img src={img} alt="" />
                                        </div>
                                        <div className="col-8 pt-3">
                                            <div>{name}</div>
                                            <div>{price} $</div>
                                        </div>

                                    </div>
                                )
                            })
                        }


                    </div>
                </div>
            </div>
        </div>
    )
}

export default Popular;
